package lecture01;

import java.util.*;

public class Table_of_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int i = 1;
		while (i <= n) {
			int p = 7 * i;
			System.out.println(p);
			i++;
			
		}
	}

}
