package lecture02;
import java.util.*;
public class Print_n_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int i = 1;
		while (i<=n) {
			System.out.println(i);
			i++;
		}
	}

}
